mapper.select:
select au.*,ar.roleName roleName from as_user au
INNER JOIN as_role ar ON au.roleId=ar.id
where userCode=#{userCode} and userPassword=#{userPassword} and au.isStart=1;

select count(1) from as_user 

	userCode like concat('%',#{userCode},'%')
	and userName like concat('%',#{userName},'%')
	and roleId=#{roleId}
	and isStart=#{isStart}

select au.*,ar.roleName roleName from as_user au
INNER JOIN as_role ar ON au.roleId=ar.id

	au.userCode like concat('%',#{user.userCode},'%')
	and au.userName like concat('%',#{user.userName},'%')
	and au.roleId=#{user.roleId}
	and au.isStart=#{user.isStart}

limit #{pageIndex},#{pageSize}
